.products {
  padding-top: 121px;
  padding-bottom: 98px;

  @include mq(tablet) {
    padding-top: 122px;
    padding-bottom: 112px;
  }

  @include mq(desctop) {
    padding-top: 127px;
    padding-bottom: 129px;
  }
}

.products__list {
  display: flex;
  justify-content: center;
  @include mq(mobile-only) {
    margin-top: 147px;
    flex-wrap: wrap;
  }
  @include mq(tablet) {
    margin-top: 150px;
    flex-basis: calc(100% / 3 - 40px);
  }

  @include mq(desktop) {
    margin-top: 259px;
    flex-basis: calc(100% / 3 - 54px);
  }
}

.products__item {
  max-width: 280px;
  height: auto;
  position: relative;
  border-radius: 24px;
  text-align: center;
  @include mq(mobile-only) {
    padding: 158px 40px 44px;
    &:not(:last-child) {
      margin-bottom: 127px;
    }
  }

  @include mq(mobile) {
    width: 280px;
  }

  @include mq(tablet-only) {
    padding: 152px 11px 40px;
    width: 220px;
    &:not(:last-child) {
      margin-right: 20px;
    }
  }

  @include mq(desktop) {
    padding: 163px 44px 50px;
    min-width: 330px;
    &:not(:last-child) {
      margin-right: 27px;
    }
  }

  &::before {
    content: ' ';
    position: absolute;
    top: -23%;
    left: 50%;
    transform: translateX(-50%);
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    width: 249px;
    height: 252px;

    @include mq(tablet) {
      width: 220px;
      height: 226px;
    }

    @include mq(desktop) {
      width: 335px;
      height: 335px;
      top: -40%;
    }
  }
}

.products__item--first {
  background-color: var(--bgc-third);
  &::before {
    @include mq(desktop) {
      background-image: url('../images/product/ice-cream-x1.png');
      @include mq(retina) {
        background-image: url('../images/product/ice-cream-x2.png');
      }
    }
    @include mq(tablet-only) {
      background-image: url('../images/product/ice-cream-tab-x1.png');
      @include mq(retina) {
        background-image: url('../images/product/ice-cream-tab-x2.png');
      }
    }
    @include mq(mobile-only) {
      background-image: url('../images/product/ice-cream-mob-x1.png');
      @include mq(retina) {
        background-image: url('../images/product/ice-cream-mob-x2.png');
      }
    }
  }
}

.products__item--second {
  background-color: var(--bgc-fourth);
  &::before {
    @include mq(desktop) {
      background-image: url('../images/product/ice-coffee-x1.png');
      @include mq(retina) {
        background-image: url('../images/product/ice-coffee-x2.png');
      }
    }
    @include mq(tablet-only) {
      background-image: url('../images/product/ice-coffee-tab-x1.png');
      @include mq(retina) {
        background-image: url('../images/product/ice-coffee-tab-x2.png');
      }
    }
    @include mq(mobile-only) {
      background-image: url('../images/product/ice-coffee-mob-x1.png');
      @include mq(retina) {
        background-image: url('../images/product/ice-coffee-mob-x2.png');
      }
    }
  }
}

.products__item--third {
  background-color: var(--bgc-fifth);
  &::before {
    @include mq(desktop) {
      background-image: url('../images/product/milk-shake-x1.png');
      @include mq(retina) {
        background-image: url('../images/product/milk-shake-x2.png');
      }
    }
    @include mq(tablet-only) {
      background-image: url('../images/product/milk-shake-tab-x1.png');
      @include mq(retina) {
        background-image: url('../images/product/milk-shake-tab-x2.png');
      }
    }
    @include mq(mobile-only) {
      background-image: url('../images/product/milk-shake-mob-x1.png');
      @include mq(retina) {
        background-image: url('../images/product/milk-shake-mob-x2.png');
      }
    }
  }
}

.products__title {
  font-family: Titan One;
  font-weight: normal;
  text-align: center;
  text-transform: uppercase;
  color: var(--text-fourth-color);
  @include font-size(20, 23, 0.04);

  @include mq(desktop) {
    @include font-size(30, 34, 0.04);
  }

  &::after {
    content: ' ';
    display: flex;
    margin: 30px auto;
    background-image: url('../images/product/points1x.png');
    @include mq(retina) {
      background-image: url('../images/product/points2x.png');
    }
    width: 26px;
    height: 6px;
    background-size: contain;
    background-color: transparent;
    @include mq(desktop) {
      margin: 32px auto;
    }
  }
}

.products__text {
  margin-bottom: 30px;
  font-weight: 700;
  color: var(--text-fourth-color);
  text-align: center;
  @include font-size(14, 21, 0.04);

  @include mq(tablet) {
    margin-bottom: 40px;
    @include font-size(14, 25, 0.04);
  }

  @include mq(desctop) {
    margin-bottom: 62px;
    @include font-size(16, 28, 0.04);
  }
}

.products__btn {
  margin: 0 auto;
  &:hover,
  &:focus {
    background-color: var(--button-second-hover-color);
    color: var(--text-fourth-color);
  }
  transition: background-color var(--transition-time) var(--transition-function),
    color var(--transition-time) var(--transition-function);
}
